import { typeOf } from '../utility/index';


/**
 * @param {Number} [min=-Infinity]
 * @param {Number} [max=+Infinity]
 * @param {Number} [step=1]
 *
 * @return {ModelValueHandler}
 */
export function Range(min, max, step = 1) {

    min = (min != null)  ?  min  :  -Infinity,
    max = (max != null)  ?  max  :  +Infinity;

    return  (value, key, data) => {

        if ((min > value)  ||  (value > max)  ||  ((value - min) % step))
            throw RangeError(
                `"${key}" of ${typeOf( data )} should be in range of [${min}, ${max}] with step ${step}`
            );
    };
}

/**
 * @type {RegExp}
 */
export const Email = /^.+?@(.+?\.){1,}\w+$/;

/**
 * @type {RegExp}
 */
export const URI = /^\w+:\S+$/;

/**
 * @type {RegExp}
 */
export const Phone = /^[0-9+-]{7,}$/;


/**
 * @param {*}      value
 * @param {String} key
 * @param {Model}  data
 *
 * @throw {SyntaxError}
 */
export function IPv4(value, key, data) {

    value = (value + '').split('.').filter( Boolean );

    if (
        (value.length !== 4)  ||
        !value.every(number => (
            /^[\dxA-Fa-f]+$/.test( number )  &&
            (number > -1  &&  number < 256)
        ))
    )
        throw SyntaxError(
            `"${key}" of ${typeOf( data )} should be an IPv4 address`
        );
}


/**
 * @param {*}      value
 * @param {String} key
 * @param {Model}  data
 *
 * @throw {SyntaxError}
 */
export function IPv6(value, key, data) {

    const v4 = (/^::ffff:(\S+)/i.exec( value ) || '')[1];

    if ( v4 )
        try {  IPv4(v4, key, data);  return;  } catch (error) {/**/}

    else if (! (/::/g.exec( value ) || '')[1]) {

        value = value.split( /::|:/ );

        if (
            (value.length > 1 && value.length < 9)  &&
            value.every(number => {

                number = parseInt(number, 16);

                return (
                    /^[\da-fA-F]+$/.test( number )  &&
                    (number > -1  &&  number < 65536)
                );
            })
        )
            return;
    }

    throw SyntaxError(
        `"${key}" of ${typeOf( data )} should be an IPv6 address`
    );
}


/**
 * Validator for IP address
 *
 * @param {*}      value
 * @param {String} key
 * @param {Model}  data
 *
 * @throw {SyntaxError}
 */
export function IPA(value, key, data) {
    try {
        IPv4(value, key, data);

    } catch (error) {
        try {
            IPv6(value, key, data);

        } catch (error) {

            throw SyntaxError(
                `"${key}" of ${typeOf( data )} should be an IP address`
            );
        }
    }
}


/**
 * @param {*}      value
 * @param {String} key
 * @param {Model}  data
 *
 * @throw {URIError}
 */
export function HTTP_URL(value, key, data) {

    const [_, domain, port] =  // eslint-disable-line
        /^(?:https?:)?\/\/(?:\S+?(?::\S+?)?@)?([\w-.]+)(?::(\d{1,5}))?/
            .exec( value )  ||  [ ];

    if (port > -1  &&  port < 65536) {

        if (/\.\d\w+$/.test(domain || ''))
            try {  IPA(domain, key, data);  return;  } catch (error) {/**/}
        else
            return;
    }

    throw URIError(
        `"${key}" of ${typeOf( data )} should be a HTTP URL`
    );
}

const { toString } = Object.prototype;

/**
 * @param {*} object
 *
 * @return {String} Class name
 */
export function typeOf(object) {

    return  object.constructor.name  ||  toString.call( object ).slice(8, -1);
}


/**
 * @param {Function} func
 *
 * @return {Boolean}
 */
export function isClass(func) {  return  /^class\W/.test(func + '');  }


/**
 * @param {*} object
 *
 * @yield {Object}
 */
export function* walkPrototype(object) {

    while (object = Object.getPrototypeOf( object ))  yield object;
}


/**
 * @typedef {Object} DecoratorDescriptor
 *
 * @property {String}                kind         - `class`, `field` or `method`
 * @property {String}                [key]        - Member name
 * @property {String}                [placement]  - `static` or `prototype`
 * @property {Object}                [descriptor] - Last parameter of `Object.defineProperty()`
 * @property {DecoratorDescriptor[]} [elements]   - Class members
 */

import { typeOf, isClass } from './utility/index';

import Model from './Model';

import { trigger } from './hook/index';


const base_type = [Number, String, Boolean, Date, RegExp, Object, Array];

function check(value, condition, key, data) {

    const type = typeOf( data );

    if (base_type.includes( condition )  ||  isClass( condition )) {

        if (Object(value) instanceof String)  try {

            value = JSON.parse( value );

        } catch (error) {/**/}

        value = Object( value );

        if (
            (condition === Object)  ?
                (value.constructor !== Object)  :  !(value instanceof condition)
        )
            throw TypeError(`"${key}" of ${type} should be ${condition.name}`);

    } else if (condition instanceof Function) {
        /**
         * Throw an Error is recommended when something is wrong
         *
         * @typedef {function(value: *, key: String, data: Model): void} ModelValueHandler
         */
        condition(value, key, data);

    } else if (condition instanceof RegExp) {

        if (! condition.test(value + ''))
            throw TypeError(`"${key}" of ${type} should match /${condition.source}/`);

    } else if (condition instanceof Array)
        if (! condition.includes( value ))
            throw TypeError(`"${key}" of ${type} should be in ${condition.join(', ')}`);
}

/**
 * Field Decorator to validate the value of Setter
 *
 * @param {Function|RegExp|Array|ModelValueHandler} condition
 * @param {*}                                       [defaultValue]
 *
 * @return {function(meta: DecoratorDescriptor): void}
 */
export function is(condition, defaultValue) {

    return  meta => {

        const { kind, placement, key, descriptor } = meta;

        const origin = descriptor.set;

        descriptor.set = function (value) {

            value = (value != null) ? value : defaultValue;

            if (!(value != null))  return this.delete( key );

            if (condition.prototype instanceof Model)
                value = new condition( value );
            else
                check(value, condition, key, this);

            origin.call(this, value);
        };

        meta.finisher = Class => {

            trigger('is',  {Class, kind, placement, key, descriptor});
        };
    };
}

# Data Scheme

**Data Model** framework based on [ECMAScript Decorator proposal](https://github.com/tc39/proposal-decorators)

